import React, { Fragment, useState } from "react";
import { HiSearch } from "react-icons/hi";
import "../Styles/NavBarStyle.css";
import { Routes, Route, NavLink } from "react-router-dom";
import Movies from "./Movies";

import { NavDropdown } from "react-bootstrap";

export const Container = React.createContext();


const genre = [
    'Action', 'Adventure', 'Comedy', 'Drama', 'Horror'
]

function Navbar() {
  const [toggle, setToggle] = useState(true);
  const [search, setSearch] = useState([]);


  const Api = "https://api.themoviedb.org/3"

  const SearchMovies = () => {
    const data  = axios.post(Api+'/search/movie',{
        params:{
            api_key: 'ba8f4caa4e6ebec49f8c6b8ba603ed04',
        },
        query: search

    })

    const results = data.data.results
    setMoviesData(results)
}
  return (
    <Container.Provider value={toggle}>
      <Fragment>
        <nav className="navBarColor nav-bar">
          <div className="nav-options">
            <h1 id="heading">7Flix</h1>
            <NavLink to="">
              <span id="MoviesLight">Movies</span>
            </NavLink>
            <NavDropdown
              id="nav-dropdown-dark-example"
              title="KATEGORI"
              menuVariant="dark"
            >
                {genre.map(item => 
              <NavDropdown.Item href={`#${item}`}>{item}</NavDropdown.Item>
              )}
            </NavDropdown>
          </div>
          <div className="input-grouped">
            <input type="text" placeholder="Search movies" onChange={setSearch}></input>
            <HiSearch fontSize={21} color="grey" id="search" />
          </div>
        </nav>
        <Routes>
          <Route path="" element={<Movies />} />
        </Routes>
      </Fragment>
    </Container.Provider>
  );
}

export default Navbar;
